import { Icon } from "@iconify/react";
import { useTranslation } from "react-i18next";
import myProfile from '../../images/myProfile.jpeg'

const AboutMe = () => {
  const { t } = useTranslation();

  return (

    <section style={{ backgroundColor:'#b1b1b1'}} className="py-5 dark:bg-[#7f7f7f] md:py-10">
      <h2 className="text-center text-xl uppercase tracking-widest dark:text-white font-bold ">
        {t("about_me.title")}
      </h2>

      <div className="flex flex-col items-center justify-center gap-10 px-10 pb-10 md:flex-row md:pb-0">
        <div className="text-center md:w-1/3">
          <div className="mb-[30px] mt-[55px] inline-block p-4 pb-10 shadow-card ">
            <img
              src={myProfile}
              alt="Profile"
              height="250px"
              width="250px"
              className="w-[200px]"
            />
            {/* <div className="mt-3 flex justify-between">
              {t("about_me.tech_stacks", { returnObjects: true })?.map(
                (tech: string) => (
                  <Icon
                    key={tech}
                    icon={tech}
                    style={{
                      fontSize: 50,
                    }}
                  />
                ),
              )}
            </div> */}
          </div>
        </div>
        <div className="md:w-2/3">
          <div className="rounded-sm border border-[rgba(0,0,0,.125)]">
            <div className="flex items-center gap-3 border-b border-[rgba(0,0,0,.125)] bg-[rgba(0,0,0,0.03)] px-3 py-2">
              <Icon icon="emojione:red-circle" width={10} />
              <Icon icon="twemoji:yellow-circle" width={10} />
              <Icon icon="twemoji:green-circle" width={10} />
            </div>
            <div
              className="p-3 text-justify"
              style={{
                height: "auto",
                fontSize: "132%",
                lineHeight: "200%",
              }}
            >

              <span className="text-xl font-medium dark:text-white">
                {t("about_me.greeting")} :){" "}
              </span>
              <br />

              <p className="text-sm font-medium dark:text-white">
                {t("about_me.content")
                  .split("{'\n'}")
                  .map((point, index) => (
                    <p key={index}>{point}</p>
                  ))}
              </p>
              <br />
              <p className="text-sm font-medium dark:text-white">
                {t("about_me.content1")
                  .split("{'\n'}")
                  .map((point, index) => (
                    <p key={index}>{point}</p>
                  ))}
              </p>
              <br style={{ marginBottom: '30px' }}/>
              <p className="text-sm font-medium dark:text-white ">
                {t("about_me.content2")
                  .split("{'\n'}")
                  .map((point, index) => (
                    <p key={index}>{point}</p>
                  ))}
              </p>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default AboutMe;
