import { Icon } from "@iconify/react";
import { useTranslation } from "react-i18next";

const Skills = () => {
  const { t } = useTranslation();

  return (
    <section style={{ backgroundColor:'#b1b1b1'}} className=" px-3 py-5 pb-20 md:px-0">
      <h2  className="py-10 text-center text-xl font-bold uppercase tracking-widest text-white">
        {t("skills.title")}
      </h2>

      <ul className="flex flex-wrap justify-center  gap-4">
        {t("skills.icons", { returnObjects: true }).map((skill) => (
          <li
            key={skill.name}
            className="flex h-[84px] w-[100px] flex-col items-center justify-center rounded-[8px] bg-[#2A2929] p-[5x] text-white"
          >
            <Icon
              icon={skill.class}
              className="mx-auto"
              width="39.6px"
              height="39.5px"
            />
            <p className="mt-3 text-center text-xs">{skill.name}</p>
          </li>
        ))}
      </ul>
    </section>
  );
};

export default Skills;
